    let b = a.replace(')', '')
    let c = b.replace('}', '')
    let d = c.replace('{', '')

    return d

}
function Count(item, element) {

    let count = 0


    for (let i = 0; i < element.length; i++) {

        if (element.charAt(i) == item && element.charAt(i + 1) == item) {

            count += 1



        }



    }

    return count;


}


